package josesp.splash.com.rsacriptosystem;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import java.util.Random;import josesp.splash.com.rsacriptosystem.model.ExtendedEuclideanAlgorithm;import josesp.splash.com.rsacriptosystem.model.InverseMultiplication;import josesp.splash.com.rsacriptosystem.model.Nodo;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);    }    public void btnManual_onclick(View view){        Intent intent = new Intent(this,ManualActivity.class);        startActivity(intent);        finish();    }    public void btnAutomatic_onclick(View view){        int p = generateNumber();        int q = generateNumber();        int n = p * q;        int phi = (p - 1) * (q - 1);        System.out.println("P : "+ p + " Q : "+ q + " n : "+ n +" phi : "+ phi);        int e = generateE(phi);        int d = InverseMultiplication.getInverse(phi, e);        Intent intent = new Intent(this, KeysActivity.class);        intent.putExtra("n",n);        intent.putExtra("e",e);        intent.putExtra("d",d);        startActivity(intent);    }    public int generateNumber(){        return (int)((Math.random()*100)+100);    }    public int generateE(int phi){        int random = (int)((Math.random()*3));        int counter = 0;        Nodo nodo;        int i;        for(i = 11; i < phi ; i++){            nodo = ExtendedEuclideanAlgorithm.applyExtendedEuclideanAlgorithm(i, phi);            if(nodo.getD() == 1){                if(counter == random){                    return i;                }                counter++;            }        }        return -1;    }}